import { createSlice } from '@reduxjs/toolkit';

const initialState = {
    user: {
        _id: null,
        username: '',
        profilePic: '',
    },
    message: '',
    success: false
};

const authSlice = createSlice({
    name: 'auth',
    initialState,
    reducers: {
        loginSuccess: (state, action) => {
            state.user._id = action.payload.authUser._id;
            state.user.username = action.payload.authUser.userName;
            state.user.profilePic = action.payload.authUser.profilePic;
            state.message = action.payload.message;
            state.success = action.payload.success;
        },
        logout: (state) => {
            state.user = initialState.user;
            state.message = '';
            state.success = false;
        }
    }
});

export const { loginSuccess, logout } = authSlice.actions;
export default authSlice.reducer;
